from abc import ABC, abstractmethod

# --- ABSTRACTION ---
class Shape(ABC):
    @abstractmethod
    def area(self):
        pass

# --- INHERITANCE & POLYMORPHISM ---
class Square(Shape):
    def __init__(self, side):
        self.__side = side # ENCAPSULATION (Private)

    def area(self):
        return self.__side ** 2

    def get_side(self): # Getter
        return self.__side

# --- EXECUTION ---
sq = Square(5)
print(f"Area: {sq.area()}")
print(f"Side length: {sq.get_side()}")

# 1. Magic Methods
class Vector:
    def __init__(self, x, y):
        self.x = x
        self.y = y
    def __add__(self, other):
        return Vector(self.x + other.x, self.y + other.y)
    def __str__(self):
        return f"Vector({self.x}, {self.y})"

v1 = Vector(2, 4)
v2 = Vector(1, 1)
print(f"Vector Addition: {v1 + v2}")

# 2. *args Example
def sum_all(*args):
    return sum(args)

print(f"Sum of many numbers: {sum_all(1, 2, 3, 4, 10)}")

# 1. Writing and Reading safely
try:
    with open("data.txt", "w") as f:
        f.write("User ID: 101\nStatus: Active")

    with open("data.txt", "r") as f:
        # Reading line by line
        for line in f:
            print(f"Processing: {line.strip()}")
except FileNotFoundError:
    print("Error: The file was not found.")
except Exception as e:
    print(f"An error occurred: {e}")
